import React from "react"
import 'bootstrap/dist/css/bootstrap.min.css'
import "../../App.css"
import Button from 'react-bootstrap/Button';
import {Link} from "react-router-dom";
import SearchFitler from "../SearchFilter";
import PokemonBox from "./PokemonBox";

const tableabilities = {
    marginLeft: "400px",
    marginTop: "15px",
    marginRight: "100px",
}

class Pokemon extends React.Component {
    constructor(){
        super();
        this.state = {
            pokemon: [],
            pokemonJSON: ""
        }
    }

    componentDidMount() {
        // fetch from mongodb
        let doneFetching = false
        let id = 2

        while(!doneFetching){
            // fetch each pokemon and add to state
            axios.get('http://localhost:5000/pokemon/' + id)
                .then((response) => {
                    if(response.ok){
                        console.log("response was ok");
                        return response.json();
                    } else {
                        doneFetching = true;
                        console.log("failed response");
                        throw new Error("failed to get response");
                    }
                })
                .then(data => {
                    // get number of commits and update state
                    console.log(data);

                    this.setState({
                        pokemon: this.state.pokemon.concat(
                            <PokemonBox/>
                        )
                    })
                })
                .catch((err) =>{
                    console.log("Promise rejected")
                });

            doneFetching = true;
        }
    }

    render() {
        return (
            <div className="App">
                <div className="container" style={tableabilities}>
                    <h1>Pokemon</h1>
                    <br/>
                    <br/>
                    <SearchFitler/>
                    <div className="row mt-5">
                        {this.state.pokemon}
                    </div>
                </div>
            </div>

        );
    }
}

export default Pokemon;
